ee:message.ee:set-payload:
%dw 2.0
output application/xml
ns ns0 urn:messages.platform.webservices.netsuite.com
ns ns01 urn:common.platform.webservices.netsuite.com
ns ns02 urn:core.platform.webservices.netsuite.com
---
{
    ns0#search: {
        ns0#searchRecord @("xmlns:ns01": ns01, xsi#"type": "ns01:TransactionSearchBasic"): {
            ns01#lastModifiedDate @(operator: "within"): {
                ns02#searchValue: vars.invoiceRunTimeStamp as DateTime,
                ns02#searchValue2: vars.currentRunTimeStamp as DateTime
            },
            ns01#recordType @(operator: "is"): {
                ns02#searchValue: vars.recordType
            }
        }
    }
}

%dw 2.0 fun metaDataDe (customFields) = (customFields pluck ($$.@scriptId):($.value) ) reduce ((item, accumulator={}) -> item ++ accumulator )
output application/json writeAttributes=true ---
payload.result.payload.record update { case customFields at .customFieldList -> metaDataDe(customFields) case customFieldsLine at .itemList.item.customFieldList -> metaDataDe(customFieldsLine) }

ee:variables.ee:set-variable:
%dw 2.0
var internalId = payload.result.payload.record.entity.@internalId
output application/json  
---
if (internalId ~= "1328")
  {
      interchangeId: p('write.costcoDs.interchangeId'),
      partnerId: p('write.costcoDs.partnerId'),
      partnerName: p('write.costcoDs.partnerName'),
      partnerAppCode : p('write.costcoDs.partnerAppCode')
  }
else if (internalId ~= "1147")
    {
      interchangeId: p('write.costcoDepo.interchangeId'),
      partnerId: p('write.costcoDepo.partnerId'),
      partnerName: p('write.costcoDepo.partnerName'),
      partnerAppCode : p('write.costcoDepo.partnerAppCode')
  }
else if (internalId ~= "1146")
     {
      interchangeId: p('write.costcoCanada.interchangeId'),
      partnerId: p('write.costcoCanada.partnerId'),
      partnerName: p('write.costcoCanada.partnerName'),
      partnerAppCode : p('write.costcoCanada.partnerAppCode')
  }
else
  "Not a partner"
